function VisualizePost(cdata1)
%CREATEFIGURE(CDATA1)
%  CDATA1:  image cdata

%  Auto-generated by MATLAB on 15-Sep-2013 21:29:16

% Create figure
figure1 = figure;

% Create axes
axes1 = axes('Parent',figure1,'YDir','reverse','Layer','top');

[N, K]=size(cdata1);
%% Uncomment the following line to preserve the X-limits of the axes
xlim(axes1,[0.5 (K+0.5)]);
%% Uncomment the following line to preserve the Y-limits of the axes
ylim(axes1,[0.5 (N+.5)]);
box(axes1,'on');
hold(axes1,'all');

% Create image
image(cdata1,'Parent',axes1,'CDataMapping','scaled');

% Create xlabel
xlabel('Codeword Index','FontSize',12);

% Create ylabel
ylabel('Clip Index','FontSize',12);

% Create title
title('Visualizing the Topic Posterior Vectors','FontSize',14);

% Create colorbar
colorbar('peer',axes1);

